import { useSelector } from "react-redux";
import { selectBlogById, deleteBlog } from "../features/blogsSlice";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

const SoloBlog = () => {

    const { blogId } = useParams();

    const blog = useSelector((state) => selectBlogById(state, Number(blogId)));

    if (!blog) {
        return (
            <p>No Blogs here!</p>
        )
    }

    return (

        <div className="flex justify-center">
        <div>
        <div key={blog.id} className='bg-gray-300 m-4 p-3 rounded-lg'>
               <p className="text-center">{blog.body}</p>
               <div className="m-3">
                <Link to={`delete/${blog.id}`} className='bg-slate-900 rounded-lg p-2 text-white m-2'>
                    DELETE</Link>
                </div>
        </div>
        </div>
    </div>
    );
};
export default SoloBlog;
