import { useSelector } from "react-redux";
import { selectBlogById, deleteBlog } from "../features/blogsSlice";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";


const SoloBlog = () => {

    const { blogId } = useParams();

    const [requestStatus, setRequestStatus] = useState('idle');


    const blog = useSelector((state) => selectBlogById(state, Number(blogId)));

    if (!blog) {
        return (
            <p>No Blogs here!</p>
        )
    }

    const deleteHandler = () => {
        try {
            setRequestStatus('pending')
            dispatch(deleteBlog({ id: blog.id })).unwrap()

            setBody('')
            navigate('/')
        } catch (err) {
            console.log('Faild to delete blog', err)
        } finally {
            setRequestStatus('idle')
        }
    }

    return (

        <div className="flex justify-center">
        <div>
        <div key={blog.id} className='bg-gray-300 m-4 p-3 rounded-lg'>
               <p className="text-center">{blog.body}</p>
               <div className="m-3">
               <button 
                onClick={deleteHandler}
                className="bg-white text-black p-2 rounded-lg"
                type="button">DELETE</button>
                </div>
        </div>
        </div>
    </div>
    );
};
export default SoloBlog;
